import './ConstructorRowEl.scss';

import { PlusCircleOutlined, MinusCircleOutlined } from '@ant-design/icons';
import { useContext, useEffect, useState } from 'react';



const ConstructorRowEl = ({ row }) => {

    const [ columns, setColumns ] = useState(1);

    const [ layout, setLayout ] = useState({
        1: {
            id: idGen(row, 1),
            x: '1fr',
            y: columns
        },
    })

    const [ gridLayoutStyle, setGridLayoutStyle ] = useState({gridTemplateColumns: '1fr'});


    useEffect(() => {
        const gridStyle = {gridTemplateColumns: Object.values(layout).map(el => el.x).join(' ')};
        console.log(gridStyle);
        setGridLayoutStyle(gridStyle)
        // idGen();
    }, [columns, layout])

    const handleLayout = (e, i) => {
        const newValue = {...layout[i], x: e.target.value}
        setLayout(layout => {
            return ({...layout, [i]: newValue})
        })
    }

    const renderColumns = () => {
        let columnsEl = [];
        for (let i = 1; i <= columns; i ++) {
            columnsEl.push((
                <div key = {i} className="constructor-container__row__el__section">
                    <input 
                        className='constructor-container__row__el__section__input' 
                        value={`${layout[i]}`}
                        onChange={(e) => handleLayout(e, i)}></input>
                </div>
            ))
        }
        return columnsEl;
    }

    const idGen = (r, c) => {
        console.log(String(r) + String(c))
    }

    const addColumn = () => {
        if (columns < 3) {
            setColumns(prev => prev + 1)
            const newSection = {
                id: idGen(row, columns + 1),
                x: '1fr',
                y: columns + 1
            };
            setLayout({...layout, [columns+1]: newSection})   
        }
    }

    const removeColumn = () => {
        if (columns > 1) {
            setColumns(prev => prev - 1)
            setLayout({...layout, [columns]: ''})  
        } 
    }

    return (
        <div className='constructor-container__row'>
            <PlusCircleOutlined 
                        style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer'}}
                        onClick={() => addColumn()}/>
            <MinusCircleOutlined 
                style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer', marginLeft: '5px'}}
                onClick={() => removeColumn()}/>
            <div className="constructor-container__row__el" style={gridLayoutStyle}>
                {renderColumns()}
            </div>
        </div>
    );
}

export default ConstructorRowEl;
