import './ConstructorRowEl.scss';

import { PlusCircleOutlined, MinusCircleOutlined } from '@ant-design/icons';
import { useEffect, useState } from 'react';

const ConstructorRowEl = ({ row }) => {
    const [ columns, setColumns ] = useState(1);

    const [ layout, setLayout ] = useState({
        1: "1fr",
        2: '1fr'
    })

    const [ gridLayoutStyle, setGridLayoutStyle ] = useState();

    let gridStyle = {gridTemplateColumns: '4fr 2fr'}

    useEffect(() => {
        gridStyle = {gridTemplateColumns: Object.values(layout).join(' ')};
        console.log(gridStyle)
    })

    const renderColumns = () => {
        let columnsEl = [];
        for (let i = 1; i <= columns; i ++) {
            columnsEl.push((
                <div key = {i} className="constructor-container__row__el__section">
                    <input 
                        className='constructor-container__row__el__section__input' 
                        value={`${layout[i]}`}></input>
                </div>
            ))
        }
        return columnsEl;
    }

    const addColumn = () => {
        if (columns < 3) {
            setColumns(prev => prev + 1)
            console.log(columns)   
        } 
    }

    const removeColumn = () => {
        if (columns > 1) {
            setColumns(prev => prev - 1)
        } 
    }

    return (
        <div className='constructor-container__row'>
            <PlusCircleOutlined 
                        style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer'}}
                        onClick={() => addColumn()}/>
            <MinusCircleOutlined 
                style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer', marginLeft: '5px'}}
                onClick={() => removeColumn()}/>
            <div className="constructor-container__row__el" style={gridStyle}>
                {renderColumns()}
            </div>
        </div>
    );
}

export default ConstructorRowEl;
