import { useEffect, useState } from 'react';
import './Constructor.scss';

import ConstructorRowEl from '../ConstructorRowEl/ConstructorRowEl';
import SettingsMenu from '../SettingsMenu/SettingsMenu';

const Constructor = ({ rows }) => {

    const [ sections, setSections ] = useState([]);

    const [ settingstMenuOpened, setSettingsMenuOpened ] = useState(false);

    const handleSettingsMenu = () => {
        setSettingsMenuOpened(true)
    };

    const rowEls = () => {
        const rowEls = [];
        for (let i = 0; i < rows; i++) {
            rowEls.push((
                <ConstructorRowEl key = {i} row={i} handleSettingsMenu={handleSettingsMenu}/>
            ))
        } 
        return rowEls;
    }

    return (
        <div className="constructor-container">
            {rowEls()}
            {settingstMenuOpened ? <SettingsMenu /> : null}
        </div>
    );
}

export default Constructor;
