import './ConstructorRowEl.scss';

import { PlusCircleOutlined, MinusCircleOutlined, SettingOutlined } from '@ant-design/icons';
import { useContext, useEffect, useState } from 'react';



const ConstructorRowEl = ({ row }) => {

    const [ columns, setColumns ] = useState(1);

    const [ layout, setLayout ] = useState({
        1: "1fr",
    })

    const [ gridLayoutStyle, setGridLayoutStyle ] = useState({gridTemplateColumns: '1fr'});


    useEffect(() => {
        const gridStyle = {gridTemplateColumns: Object.values(layout).join(' ')};
        setGridLayoutStyle(gridStyle)
    }, [columns, layout])

    const handleLayout = (e, i) => {
        setLayout(layout => {
            return ({...layout, [i]: e.target.value})
        })
    }

    const renderColumns = () => {
        let columnsEl = [];
        for (let i = 1; i <= columns; i ++) {
            columnsEl.push((
                <div key = {i} className="constructor-container__row__el__section">
                    <SettingOutlined style={{marginLeft: '15px', marginTop: '15px', fontSize: '20px'}}/>
                    <div className=''>
                        <input 
                            className='constructor-container__row__el__section__input' 
                            value={`${layout[i]}`}
                            onChange={(e) => handleLayout(e, i)}>    
                        </input>
                        <input 
                            className='constructor-container__row__el__section__input' 
                            value={`${layout[i]}`}
                            onChange={(e) => handleLayout(e, i)}>    
                        </input>
                    </div>
                </div>
            ))
        }
        return columnsEl;
    }

    const addColumn = () => {
        if (columns < 3) {
            setColumns(prev => prev + 1)
            setLayout({...layout, [columns+1]: '1fr'})   
        }
    }

    const removeColumn = () => {
        if (columns > 1) {
            setColumns(prev => prev - 1)
            setLayout({...layout, [columns]: ''})  
        } 
    }

    return (
        <div className='constructor-container__row'>
            <PlusCircleOutlined 
                        style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer'}}
                        onClick={() => addColumn()}/>
            <MinusCircleOutlined 
                style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer', marginLeft: '5px'}}
                onClick={() => removeColumn()}/>
            <div className="constructor-container__row__el" style={gridLayoutStyle}>
                {renderColumns()}
            </div>
        </div>
    );
}

export default ConstructorRowEl;
