import './ConstructorRowEl.scss';

import { PlusCircleOutlined, MinusCircleOutlined } from '@ant-design/icons';
import { useState } from 'react';

const ConstructorRowEl = ({ row }) => {
    const [ columns, setColumns ] = useState(1);

    const renderColumns = () => {
        let columnsEl = [];
        for (let i = 0; i < columns; i ++) {
            columnsEl.push((
                <div key = {i} className="constructor-container__row-el__section">

                </div>
            ))
        }
        return columnsEl;
    }

    const addColumn = () => {
        if (columns < 3) {
            setColumns(prev => prev + 1)
        } 
    }

    const removeColumn = () => {
        if (columns > 1) {
            setColumns(prev => prev - 1)
        } 
    }

    return (
        <>
            <div className="constructor-container__row-el">
                <PlusCircleOutlined 
                    style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer'}}
                    onClick={() => addColumn()}/>
                <MinusCircleOutlined 
                    style={{alignSelf: 'center', fontSize: '30px', cursor: 'pointer', marginLeft: '5px'}}
                    onClick={() => removeColumn()}/>
                {renderColumns()}
            </div>
        </>
    );
}

export default ConstructorRowEl;
